import { createSlice } from "@reduxjs/toolkit";
const initialState = {
    cart_items : []
}

export const cartSlice = createSlice({
    name: 'Cart',
    initialState,
    reducers: {
        add_to_cart: (state,action) => {
            const item = {
                id: action.payload.id,
                title: action.payload.title,
                image: action.payload.image,
                desc: action.payload.description,
                price: action.payload.price
            }
            state.cart_items.push(item);
        },
        remove_from_cart: (state,action) => {
            if(state.cart_items.length>0)
                state.cart_items = state.cart_items.filter((item) => item.id !== action.payload.id)
        }
    }
})

export const {add_to_cart, remove_from_cart} = cartSlice.actions
export default cartSlice.reducer
